def last(n):
  return n[-1]  

def get_highs_and_lows(series):
  prev = 99999999999999
  lows, peaks = [], []
  for i, price in enumerate(series):
    if price > prev:
      peaks.append((i, price))
    if price < prev:
      lows.append((i, price))
    prev = price
  return peaks, lows

def get_best_after(series, n):
  return sorted(series, key=last, reverse=True)[:n]

def make_profit(series, n):
  highs, lows = get_highs_and_lows(series)

def maxProfit(prices, n, k, money_invested):
  profit = []
  buys = []
  solds = []
  first_buy = -1
  hs, ls = get_highs_and_lows(prices)
  for i in range(k+1):
    profit.append([0]*(n+1))
  
  for i in range(1, k+1):
    for j in range(1, n+1):
      max_so_far = 0
      max_before = 0 
      for m in range(j):
        max_so_far = max(max_so_far, prices[j] - prices[m] + profit[i-1][m])
        if max_so_far != max_before:
          append_to_buys = prices[m], m
          append_to_solds = prices[j], j
        max_before = max_so_far
      profit[i][j] = max(profit[i][j-1], max_so_far)
    buys.append(append_to_buys)
    solds.append(append_to_solds)
      
  return profit[k][n], (profit[k][n]*1.0/ls[0][1])*money_invested
  
def calculateProfit(array, transactions, money_invested):
  print(maxProfit(array, len(array)-1, transactions, money_invested))
